import {createStore} from 'redux';


const demoReducer = (state = {}, action) => {
    switch(action.type) {
        case 'ONE':
            return {
                ...state,
                value: 1
            }
        default:
            return state;
    }
}

const store = createStore(demoReducer);




export default store;
