import {shopActionTypes} from './shop.types';

const INITIAL_STATE = {};

const shopReducer = (state=INITIAL_STATE, action) => {
    switch(action.type) {
        case shopActionTypes.GET_SHOP_DATA:
            return state = action.payload;

        default:
            return state;
    }
}

export default shopReducer;
