import React from 'react';
import {HomePage} from './components/homepage/homepage.component';
import ShopPage from './components/shop/shop-page-component';
import SignUpAndSignIn from './components/sign-in-and-sign-up/sign-in-and-sign-up';
import {auth, createUserProfileDocument} from '../src/firebase/firebase.utils';
import {connect} from 'react-redux'
import Header from './components/header/header';
import store from '../src/redux/store';
import {setCurrentUser} from './redux/user/user-action';
import {Switch, Route} from 'react-router-dom';
import './App.css';



class App extends React.Component {

  unsubscribeFromAuth = null;

  componentDidMount() {
    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);
        userRef.onSnapshot(snapshot => {
          this.setState({
            currenUser: {
              id: snapshot.id,
              ...snapshot.data()
            }
          });
        });
      } else {
        this.setState({currenUser: userAuth})
      }
      console.log(this.state)
    });
  }


  render(){
    return (
      <div>
        <Header/>
        <Switch>
          <Route exact path='/' component = {HomePage} />
          <Route path='/shop' component = {ShopPage} />
          <Route path='/signin' component = {SignUpAndSignIn} />
        </Switch>
      </div>
    );
  }
}

