import {all, call} from "redux-saga/effects";
import {fetchCollectionsStart} from './shop/shop.saga';
import {userSaga} from './user/user.saga';
import {} from './'

export function* rootSaga() {
    yield all([
        call(fetchCollectionsStart),
        call(userSaga)
    ])
}
