import React from 'react';


import CustomButtom from '../custom-button/button'
import FormInput from '../formInput/formInput';

import {auth, createUserProfileDocument} from '../../firebase/firebase.utils';

import './signup.scss';



class SignUp extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            confirmPassword: '',
            displayName: '',
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({email: '', password: '', name: '', cpassword: ''});
    }

    handleChange = (event) => {
        const {value, name } = event.target;
        this.setState({[name]: value});
    }

    render() {
        const {email, displayName, password, confirmPassword} = this.state;
        return (
            <div className='sign-in'>
                <h2 className='title'>Don't have an Account</h2>
                <span>Sign up to create a new one</span>
                <form onSubmit={this.handleSubmit} className='sign-up-form'>
                    <FormInput type="text" id='name'name='name' value={displayName} handleChange={this.handleChange} label='name' required/>
                    <FormInput type="email" id='email'name='email' value={email} handleChange={this.handleChange} label='email' required/>
                    <FormInput type="password" id='password' name='password' value={password} handleChange={this.handleChange} label='password' required/>
                    <FormInput type="password" id='cpassword' name='cpassword' value={cpassword} handleChange={this.handleChange} label='confirm password' required/>
                    <CustomButtom type='submit'>sign up</CustomButtom>
                </form>
            </div>
        )
    }
}

export default SignUp;
