import {createStore, combineReducers} from 'redux';
import uuid from 'uuid';


const addExpense = () => ({
    type: 'ADD_EXPENSES',
    expenses: {
        
    }
}
)

const expensesReducer = (state = [], action) => {
    switch(action.type) {
        default:
            return state;
    }
}

const defaultFilter = {
    text: '',
    sortBy: 'date',
    startDate: undefined,
    endDate: undefined
}

const filtersReducer = (state = defaultFilter, action) => {
    switch(action.type) {
        default:
            return state;
    }
}

const demoState = {
    expenses: [{
        id: 'hdkjbskjd',
        description: 'january Rent',
        note: 'this was the final payment for that addres',
        amount: 54500,
        createdAt: 0
    }],
    filters: {
        text: 'rent',
        sortBy: 'amount',
        startDate: undefined,
        endDate: undefined
    }
}

const store = createStore(combineReducers({
    expenses: expensesReducer,
    filters: filtersReducer,
}));

store.subscribe(() => {
    console.log(store.getState());
})

console.log(store.getState());

