import {takeEvery, call, put} from 'redux-saga/effects';
import {shopActionTypes} from './shop.types';
import {firestore, convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils';
import {fetchCollectionFail, fetchCollectionSuccess} from './shop.action';

export function* fetchCollectionsStartAsync() {
    const collectionRef = yield firestore.collection("collections");
    const snapShot = yield collectionRef.get();
    const newShopData = yield call(convertCollectionsSnapshotToMap, snapShot);
    yield put(fetchCollectionSuccess)
    collectionRef.get().then(snapshot => {
        const newShopData = convertCollectionsSnapshotToMap(snapshot);
        dispatch(fetchCollectionSuccess(newShopData))
    }).catch(error => dispatch(fetchCollectionFail(error.message)))
}

export function* fetchCollectionsStart() {
    yield takeEvery(shopActionTypes.FETCH_COLLECTION_START,fetchCollectionsStartAsync)
}
