import React from 'react';

import SignIn from '../sign-in/sign-in'

import FormInput from '../formInput/formInput';

import './signup.scss';



class SignUp extends SignIn{
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            name: '',
        }
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>Don't have an Account</h2>
                <span>Sign up to create a new one</span>
                <form onSubmit={this.handleSubmit}>
                    <FormInput type="text" id='name'name='name' value={this.state.name} handleChange={this.handleChange} label='name' required/>
                    <FormInput type="email" id='email'name='email' value={this.state.email} handleChange={this.handleChange} label='email' required/>
                    <FormInput type="password" id='password' name='password' value={this.state.password} handleChange={this.handleChange} label='password' required/>
                    <input type="submit" value='Submit Form'/>
                </form>
            </div>
        )
    }
}

export default SignUp;
