import cartActionTypes from './cart.types';

const INITIAL_STATE = {
    hidden: true,
    cartItems: []
}

function sameCartItem(cartItems, item) {
    return cartItem.map(cartItem => cartItem.id === item.id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem)
}

function AddItems(cartItems, item) {
    const existingCartItem = cartItems.find(cartItem => cartItem.id === item.id);

    if (existingCartItem) {
        sameCartItem(cartItems, item)
    }

    return [...cartItems, {...item}]
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case cartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden,
            }
        case cartActionTypes.ADD_ITEMS:
            return {
                ...state,
                cartItems: AddItems(state.cartItems, action.payload)
            }
        case cartActionTypes.INCREASE_QUANTITY:
            return {
                ...state,
                cartItems: [{...state.cartItems.map(item=> item.id === action.payload.id?{...item, quantity: item.quantity + 1}:item)}]
            }
        case cartActionTypes.DECREASE_QUANTITY:
            return {
                ...state,
                cartItems: [{...state.cartItems.map(item=> item.id === action.payload.id?{...item, quantity: item.quantity - 1}:item)}]
            }
            
        default:
            return state;
    }
}

export default cartReducer;
