import {combineReducers} from 'redux';
import {persistStore} from 'redux-persist'
import userReducer from '../redux/user/user.reducer';
import cartReducer from '../redux/cart/cart.reducer';

export default combineReducers({
    user: userReducer,
    cart: cartReducer,
})
