  render(){
    return (
      <div>
        <Header currentUser = {this.state.currenUser}/>
        <Switch>
          <Route exact path='/' component = {HomePage} />
          <Route path='/shop' component = {ShopPage} />
          <Route path='/signin' component = {SignUpAndSignIn} />
        </Switch>
      </div>
    );
  }
}

export default App;
