import {createStore, combineReducers} from 'redux';


const addExpense = ({description = '', note = '', amount = 0, createdAt = 0} = {}) => ({
    type: 'ADD_EXPENSES',
    expenses: {
        id: '00000000',
        description,note,
        amount,
        createdAt
    }
}
)

const expensesReducer = (state = [], action) => {
    switch(action.type) {
        case 'ADD_EXPENSES':
            return [
                ...state,
                action.expenses
            ]
        default:
            return state;
    }
}

const defaultFilter = {
    text: '',
    sortBy: 'date',
    startDate: undefined,
    endDate: undefined
}

const filtersReducer = (state = defaultFilter, action) => {
    switch(action.type) {
        default:
            return state;
    }
}

const demoState = {
    expenses: [{
        id: 'hdkjbskjd',
        description: 'january Rent',
        note: 'this was the final payment for that addres',
        amount: 54500,
        createdAt: 0
    }],
    filters: {
        text: 'rent',
        sortBy: 'amount',
        startDate: undefined,
        endDate: undefined
    }
}

const store = createStore(combineReducers({
    expenses: expensesReducer,
    filters: filtersReducer,
}));

store.subscribe(() => {
    console.log(store.getState());
})
const expenseone = store.dispatch(addExpense({
    description: 'rent', amount: 100
}))

store.dispatch(addExpense({
    description: 'coffee', amount: 300
}))


export default store;
