import styled, {css} from 'styled-components';

import CustomButton from '../custom-button/button';

let isHover = false;

const ImageHover = css`
    opacity: 0.8;
`;
const customButtonHover = css`
    display: inline;
    opacity: 0.85;
`;

const imageChecker = () => {
    return isHover?ImageHover:null;
}
const buttonChecker = () => {
    return isHover?customButtonHover:null;
}



export const ImageContainer = styled.div`
    width: 100%;
    height: 95%;
    background-size: cover;
    background-position: center;
    margin-bottom: 5px;
    ${imageChecker}
`;

export const CollectionFooterContainer = styled.div`
    height: 5%;
    width: 100%;
    display: flex;
    justify-content: space-between;
    font-size: 18px;
`;

export const CustomButtonContainer = styled(CustomButton)`
    width: 80%;
    opacity: 0.7;
    position: absolute;
    top: 255px;
    display: none;
    border: none;
    outline: none;
    ${buttonChecker}
`;


export const CollectionItemContainer = styled.div`
    width: 22vw;
    display: flex;
    flex-direction: column;
    height: 400px;
    align-items: center;
    position: relative;
    cursor: pointer;
    margin: 20px 10px 40px;

    &:hover{
        ${() => {
            isHover = true
            console.log(isHover)
        }};
    }
    ${() => {
        isHover = true
        console.log(isHover)
    }};
`;
