import React from 'react';

import FormInput from '../formInput/formInput'

import './sign-in.scss';


class SingIn extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        }
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.setState({email: '', password: ''});
    }

    handleChange = (event) => {
        const {value, name } = event.target;
        this.setState({[name]: value});
    }

    render() {
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handleSubmit}>
                    <FormInput type="email" id='email'name='email' value={this.state.email} handleChange={this.handleChange} required/>
                    <label htmlFor="email">Email</label>
                    <FormInput type="password" id='password' name='password' value={this.state.password} handleChange={this.handleChange} required/>
                    <label htmlFor="password">Password</label>
                    <input type="submit" value='Submit Form'/>
                </form>
            </div>
        )
    }
}

export default SingIn;
