import React from 'react';
import './checkout.item.scss';
import {increaseQuantity, decreaseQuantity, removeItem} from '../../redux/cart/cart.action';
import {connect} from 'react-redux';

const CheckoutItem = ({cartItem: {name, imageUrl, price, quantity}, dispatch, cartItem}) => (
    <div className='checkout-item'>
        <div className="image-container">
            <img src={imageUrl} alt="item"/>
        </div>
        <span className='name'>{name}</span>
        <span className='quantity'>
            <span className='symbol'onClick={() => dispatch(decreaseQuantity(cartItem))}>{'<'}</span>
            {quantity}
            <span className='symbol' onClick={() => dispatch(increaseQuantity(cartItem))}>{'>'}</span>
            </span>
        <span className='price'>{price}</span>
        <div className='remove-button' onClick>&#10005;</div>
    </div>
)

export default connect(null)(CheckoutItem);
