import {createStore} from 'redux';


const demoReducer = (state = {}, action) => {
    switch(action.type) {
        case 'ONE':
            return {
                ...state,
                value: state.value === undefined ? 1 : state.value + 1
            }
        case 'TWO':
            return {
                ...state,
                value: state.value + 2
            }
        default:
            return state;
    }
}

const store = createStore(demoReducer);

store.subscribe(() => {
    console.log(store.getState());
})

store.dispatch({
    type: 'ONE'
})

store.dispatch({
    type: 'TWO'
})



export default store;
