import {createSelector} from 'reselect';
import {getCollections} from '../../firebase/firebase.utils'

const getState = state => state;

export const selectShop = createSelector([getState], (state) => state.shop);

export const getShop = getCollections();

getShop();


export const selectShopAsArray = createSelector([selectShop], shop => {
    return Object.values(shop);
})

export const selectCollection = collectionUrlParams => createSelector([selectShop], shop => shop[collectionUrlParams]);
