import React from 'react';
import CollectionItem from '../collection-item/collectionItem';
import {selectShop} from '../../redux/shop/shop.selector';
import {createStructuredSelector} from 'reselect';
import {connect} from 'react-redux'
import './collection.scss';


const collectionPage = ({match, CollectionItems}) => {
    return (
    <div className="collection">
        {CollectionItems.map(item => {
            console.log(item)
            if (item.title === match.params.collectionId){
                return item.items.map(element => (<CollectionItem item={element}/>))
            }
        })}
    </div>
)};

const mapStateToprops = createStructuredSelector({
    CollectionItems: selectShop,
})

export default connect(mapStateToprops)(collectionPage);
