import {createSelector} from 'reselect';

const COLLECTION_TO_MAP = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    mens: 5
}


const getState = state => state;

export const selectShop = createSelector([getState], (state) => state.shop);

export const selectCollection = collectionUrlParams => createSelector([selectShop], shop => console.log(shop));
