import React from 'react';

import {connect} from 'react-redux';

import {toggleCartHidden} from '../../redux/cart/cart.action'

import './cart.scss';


const Cart = ({toggleCartHidden, numberOfCartItems}) => (
    <div className='cart-item' onClick={toggleCartHidden}>
        <i className="fas fa-shopping-cart cart-logo"></i>
        <div className='cart-count-container'>
<span className='cart-count'>{numberOfCartItems}</span>
        </div>
    </div>
)

// const mapStateToProps = state => ({
//     numberOfCartItems: state.cart.cartItems.length,
// })

const mapDispatchToProp = (dispatch) => ({
    toggleCartHidden: () => dispatch(toggleCartHidden()),
})

export default connect(null, mapDispatchToProp)(Cart);
