import {createSelector} from 'reselect';

export const selectShop = state => state.shop;

export const selectShopItem = createSelector([selectShop], (shop) => {
    console.log(shop);
    return shop.items;
});
export const selectShopTitle = createSelector([selectShop], (shop) => shop.title);
